import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBell } from '@fortawesome/free-solid-svg-icons'
import 'bootstrap/dist/css/bootstrap.css';
import {
	Collapse,
	Navbar,
	NavbarBrand,
	NavbarToggler,
	Nav,
	NavItem,
	Button, UncontrolledPopover, PopoverHeader, Popover, PopoverBody
} from "reactstrap";

class Notification extends React.Component {
  constructor(props) {
		super(props);
console.log("props",props)
		this.state = {
      notificationData:4,
    	popoverOpen: false,
      className:this.props.className,
      message:this.props.message,
      number:this.props.number
    }
  }
  popoverToggle = () => {

    this.setState({
      popoverOpen: !this.state.popoverOpen
    });

}
  render(){
    return(
      <div>
          <FontAwesomeIcon icon={faBell} />
          <div className={this.state.className} id="NotificationPopover" >
                    {this.state.number}
                  </div>

                  					<Popover className="NotificationPopover" placement="bottom" isOpen={this.state.popoverOpen} target="NotificationPopover" toggle={this.popoverToggle} size="xl" >
                  						<PopoverHeader>
                  							Notifications
                  							<span onClick={this.popoverToggle} style={{ marginLeft: '410px ' }}> <FontAwesomeIcon icon="times" className="version" /></span>
                  						</PopoverHeader>
                  						<PopoverBody>


                                 {this.state.message}

                  						</PopoverBody>
                  					</Popover>
      </div>
    )
  }
}
export default Notification;
