import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBell } from '@fortawesome/free-solid-svg-icons'
import 'bootstrap/dist/css/bootstrap.css';
import {
	Collapse,
	Navbar,
	NavbarBrand,
	NavbarToggler,
	Nav,
	NavItem,
	Button, UncontrolledPopover, PopoverHeader, Popover, PopoverBody
} from "reactstrap";

class Notification extends React.Component {
  constructor(props) {
		super(props);

		this.state = {
      notificationData:4,
    	popoverOpen: false,
      bgColor:this.props.bgColor
    }
  }
  popoverToggle = () => {

    this.setState({
      popoverOpen: !this.state.popoverOpen
    });

}
  render(){

    return(
      <div>



          <FontAwesomeIcon icon={faBell} />
          <div  className= "notificationNumber"  style={{backgroundColor:this.state.bgColor}}id="NotificationPopover" >
                    {this.state.notificationData}
                  </div>

                  					<Popover placement="bottom" isOpen={this.state.popoverOpen} target="NotificationPopover" toggle={this.popoverToggle} size="xl" >
                  						<PopoverHeader>
                  							Notifications
                  							<span onClick={this.popoverToggle} style={{ marginLeft: '410px ' }}> <FontAwesomeIcon icon="times" className="version" /></span>
                  						</PopoverHeader>
                  						<PopoverBody>


                                 Data

                  						</PopoverBody>
                  					</Popover>
      </div>
    )
  }
}
export default Notification;
