import React from 'react';
import logo from './logo.svg';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBell } from '@fortawesome/free-solid-svg-icons'
import 'bootstrap/dist/css/bootstrap.css';
import {
	Collapse,
	Navbar,
	NavbarBrand,
	NavbarToggler,
	Nav,
	NavItem,
	Button, UncontrolledPopover, PopoverHeader, Popover, PopoverBody
} from "reactstrap";
import Notification from './components/Notification/Notification';
class App extends React.Component {
  constructor(props) {
		super(props);

		this.state = {
      notificationData:4,
    	popoverOpen: false,
      infoMessage:'This info  contains 4 Notification',
      errorMessage:'This Error contains 5 Notification',
      warningMessage:'This Warning contains 9 Notification',
      infoNumber:4,
      errorNumber:5,
      warningNumber:9
    }
  }
  popoverToggle = () => {

    this.setState({
      popoverOpen: !this.state.popoverOpen
    });

}
  render(){

    return(
       <div id="container">
      <h4 style={{textAlign:'center'}}>
          Notification Project
          </h4>
          <div className="prentClass" style={{textAlign:'center'}}>
          <div className="childClass"> <div>Info Notification</div> </div>
          <div className="childClass">
           <Notification className='infonotificationNumber' message={this.state.infoMessage} number={this.state.infoNumber} id="info"/>
           </div>
          </div>
          <div className="prentClass" style={{textAlign:'center'}}>
          <div className="childClass"> <div>Warning Notification</div> </div>
          <div className="childClass">
           <Notification className='warningnotificationNumber'  message={this.state.warningMessage} number={this.state.warningNumber} id='warning'/>
           </div>
          </div>

          <div className="prentClass" style={{textAlign:'center'}}>
          <div className="childClass"> <div>Error Notification</div> </div>
          <div className="childClass">
           <Notification className='errornotificationNumber'  message={this.state.errorMessage} number={this.state.errorNumber} id='err'/>
           </div>
          </div>

      </div>
    )
  }
}

export default App;
